export function Logo({
  className,
  classNamesForText,
  classNamesForSubtext,
  subtextVisibility = null,
  variant = 'informalSystems',
  ...otherProps
}: LogoProps) {
  const {
    classNamesForSubtext: variantClassNamesForSubtext,
    classNamesForText: variantClassNamesForText,
    logoType,
    subText,
    subtextVisibility: subtextVisibilityForVariant,
    viewBox,
  } = pathsByVariant[variant]

  const isShowingSubText =
    subtextVisibility ||
    (subtextVisibilityForVariant && subtextVisibility !== false)

  return (
    <div
      className={twMerge(`w-full`, className)}
      {...otherProps}
    >
      <svg
        width="100%"
        viewBox={viewBox}
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        {isShowingSubText &&
          subText.map((path, index) => {
            const props = typeof path === 'string' ? { d: path } : path

            return (
              <path
                {...props}
                className={twMerge(
                  variantClassNamesForSubtext,
                  classNamesForSubtext,
                )}
                key={index}
              />
            )
          })}
        <g className={twMerge(variantClassNamesForText, classNamesForText)}>
          {logoType.map((path, index) => {
            const props = typeof path === 'string' ? { d: path } : path

            return (
              <path
                key={index}
                {...props}
              />
            )
          })}
        </g>
      </svg>
    </div>
  )
}
